const formData = $('On form submission1').first().json ?? {};
const defaults = $('Get Article Metadata1').first().json ?? {};
const countryItems = $('Load Country Codes').all();              // 238 items
const countryTable = countryItems.map(i => i.json);              // [{country_code, country_name}, ...]

// --------- config ----------
const allowedKeys = ['url','focus_keyword','country','period_length','target_keywords','language'];

const keyMapping = {
  'URL': 'url',
  'Focus Keyword': 'focus_keyword',
  'Country': 'country',
  'Period Length': 'period_length',
  'Target Keywords': 'target_keywords',
  'Language': 'language',
};

// --------- helpers ----------
const toArray = (v) => {
  if (Array.isArray(v)) return v.filter(Boolean).map(s => String(s).trim()).filter(Boolean);
  if (v === undefined || v === null) return [];
  return String(v)
    .split(',')
    .map(s => s.trim())
    .filter(Boolean);
};

function normalizeCountry(input, table) {
  if (!input) return '';
  const s = String(input).trim();

  // if already a 2-letter code, trust it if present in table
  if (/^[A-Za-z]{2}$/.test(s)) {
    const hit = table.find(r => r.country_code.toLowerCase() === s.toLowerCase());
    return hit ? hit.country_code : s.toLowerCase();
  }

  // --- START: ALIAS MAPPING LOGIC (NOW IN VALID JAVASCRIPT) ---
  const aliasMap = {
    'us': 'united states',
    'usa': 'united states',
    'united states of america': 'united states',
    'uk': 'united kingdom',
    'gb': 'united kingdom',
    'uae': 'united arab emirates',
    'bharat': 'india'
  };

  const normalizedInput = s.toLowerCase();
  const canonicalName = aliasMap[normalizedInput] || normalizedInput;
  // --- END: ALIAS MAPPING LOGIC ---

  // try name match (case-insensitive) --- MODIFIED TO USE ALIAS ---
  const hitByName = table.find(r => r.country_name.toLowerCase() === canonicalName);
  if (hitByName) return hitByName.country_code;

  // try partial name match
  const hitPartial = table.find(r => r.country_name.toLowerCase().includes(s.toLowerCase()));
  if (hitPartial) return hitPartial.country_code;

  return s; // fallback (leave as-is)
}

function getFormValue(stdKey) {
  const formKey = Object.keys(keyMapping).find(k => keyMapping[k] === stdKey);
  return formKey ? formData[formKey] : undefined;
}

// --------- merge logic ----------
const mergedData = allowedKeys.reduce((acc, key) => {
  const formVal = getFormValue(key);
  const defaultVal = defaults[key];

  if (key === 'target_keywords') {
    const arr = toArray(formVal ?? defaultVal);
    acc[key] = arr;
} else if (key === 'country') {
  const formCountry = (formVal ?? '').toString().trim();
  const defaultCountry = (defaultVal ?? '').toString().trim();
  const countryIn = formCountry || defaultCountry; // Prioritize form, then default
  acc[key] = normalizeCountry(countryIn, countryTable);
} 
  else if (key === 'url') {
    let urlValue = (formVal ?? defaultVal ?? '').toString().trim();
    if (urlValue && !urlValue.startsWith('http')) {
      urlValue = 'https://' + urlValue;
    }
    acc[key] = urlValue;
  }
  else {
    acc[key] = (formVal !== undefined && formVal !== null && String(formVal) !== '')
      ? formVal
      : (defaultVal ?? '');
  }

  return acc;
}, {});

// --------- validation ----------
const required = ['url','focus_keyword','country','period_length','language']; // target_keywords may be []
const emptyFields = required.filter(k => {
  const v = mergedData[k];
  return v === undefined || v === null || String(v).trim() === '';
});

if (emptyFields.length > 0) {
  throw new Error(`Required fields are missing: ${emptyFields.join(', ')}`);
}

// --------- output ----------
return [{ json: { article_data: mergedData } }];

// Create an array to hold the cleaned items
const cleanedArray = [];

// Loop through all input items
for (const item of $input.all()) {
  // Create a copy of the item's JSON
  const cleanedItem = {...item.json};

  // Filter organic results to remove any with youtube.com in the link
  if (cleanedItem.organic) {
    cleanedItem.organic = cleanedItem.organic.filter(result => 
      !result.link.includes('youtube.com')
    );

    // Also filter any sitelinks that might contain youtube.com
    for (const result of cleanedItem.organic) {
      if (result.sitelinks) {
        result.sitelinks = result.sitelinks.filter(sitelink => 
          !sitelink.link.includes('youtube.com')
        );
      }
    }
  }

  // Filter related searches if needed
  if (cleanedItem.relatedSearches) {
    cleanedItem.relatedSearches = cleanedItem.relatedSearches.filter(search => 
      !search.query.includes('youtube.com')
    );
  }

  // Add the cleaned item to our array
  cleanedArray.push(cleanedItem);
}

// Return the cleaned array
return cleanedArray;

// Create an array to hold the cleaned items
const cleanedArray = [];

// Loop through all input items
for (const item of $input.all()) {
  // Create a copy of the item's JSON
  const cleanedItem = {...item.json};

  // Filter organic results to remove any with youtube.com in the link
  if (cleanedItem.organic) {
    cleanedItem.organic = cleanedItem.organic.filter(result => 
      !result.link.includes('youtube.com')
    );

    // Also filter any sitelinks that might contain youtube.com
    for (const result of cleanedItem.organic) {
      if (result.sitelinks) {
        result.sitelinks = result.sitelinks.filter(sitelink => 
          !sitelink.link.includes('youtube.com')
        );
      }
    }
  }

  // Filter related searches if needed
  if (cleanedItem.relatedSearches) {
    cleanedItem.relatedSearches = cleanedItem.relatedSearches.filter(search => 
      !search.query.includes('youtube.com')
    );
  }

  // Add the cleaned item to our array
  cleanedArray.push(cleanedItem);
}

// Return the cleaned array
return cleanedArray;

// Create a new array to store the simplified data
const simplifiedData = $input.all().map(item => {
  const content = item.json.message.content;

  // Return new object with only the required fields
  return {
    summary: content.short_summary,
    title:content.title,
    potential_focus_keyword: content.potential_focus_keyword,
    relevant_long_tail_keywords: content.relevant_long_tail_keywords,
    n_gram_analysis: content.n_gram_analysis
  };
});

return simplifiedData;

// Get the input array of target keywords
const keywordsArray = $('Parse Data1').first().json.article_data.target_keywords;

// Ensure we have an array to work with
if (!Array.isArray(keywordsArray)) {
  return [{ json: { keywords: '' } }];
}

// Format the array into the correct SQL string: 'keyword1', 'keyword2', 'keyword3'
const formattedKeywords = keywordsArray
  .map(keyword => `'${String(keyword).trim()}'`) // Add single quotes around each keyword
  .join(', '); // Join them with a comma and a space

// Return the final, correctly formatted string
return [{ json: { keywords: formattedKeywords } }];

function extractTopQueries(queryString) {
  return queryString
    .split(',')
    .map(q => {
      const [query, pos] = q.trim().split(' (');
      return {
        term: query,
        position: Number(pos.replace(')', ''))
      };
    })
    .slice(0, 5); // Take only top 5 from each bucket
}

function getBucketPriority(bucket) {
  switch(bucket) {
    case 'top_3': return 3;
    case 'pos_4_to_10': return 2;
    case 'pos_11_plus': return 1;
    default: return 0;
  }
}

const processedData = $input.all()
  .map(item => {
    const data = item.json;
    const topQueries = extractTopQueries(data.top_queries);

    return {
      json: {
        bucket: data.position_bucket,
        top_queries: topQueries.map(q => ({
          term: q.term,
          position: Number(q.position).toFixed(1)
        }))
      }
    };
  })
  // Sort buckets by priority
  .sort((a, b) => 
    getBucketPriority(b.json.bucket) - getBucketPriority(a.json.bucket)
  );

return processedData;

// Create a scoring and categorization system
function calculateImportanceScore(data) {
  const position = Number(data.current_position);
  const impressions = Number(data.current_impressions);
  const positionChange = Number(data.position_change);

  // Higher score = more important
  let score = 0;

  // Position scoring (higher positions get higher scores)
  score += (10 - Math.min(position, 10)) * 10;

  // Impression volume scoring
  score += Math.min(impressions / 100, 20);

  // Trend scoring (improving positions get bonus)
  score += (positionChange < 0) ? 10 : 0;

  return score;
}

function getPerformanceStatus(data) {
  const positionChange = Number(data.position_change);
  const impressionsChange = Number(data.impressions_change);
  const ctrChange = Number(data.ctr_change);

  if (positionChange < -0.5 && ctrChange > 0) return "improving";
  if (positionChange > 0.5 || (impressionsChange < 0 && ctrChange < 0)) return "declining";
  return "stable";
}

// Process the input data
const processedQueries = $input.all().map(item => {
  const data = item.json;
  const score = calculateImportanceScore(data);
  const status = getPerformanceStatus(data);

  return {
    json: {
      query: data.query,
      metrics: {
        position: Number(data.current_position).toFixed(1),
        impressions: Number(data.current_impressions),
        changes: {
          position: Number(data.position_change).toFixed(1),
          ctr: Number(data.ctr_change).toFixed(1)
        }
      },
      status,
      importance_score: Math.round(score)
    }
  };
});

// Sort by importance score
const sortedQueries = processedQueries.sort((a, b) => 
  b.json.importance_score - a.json.importance_score
);

return sortedQueries;

// First group by keyword and collect daily stats
const groupedByKeyword = {};

for (const item of $input.all()) {
  const { date, keyword, clicks, impressions, position } = item.json;

  if (!groupedByKeyword[keyword]) {
    groupedByKeyword[keyword] = {
      keyword,
      daily_stats: {}
    };
  }

  if (!groupedByKeyword[keyword].daily_stats[date]) {
    groupedByKeyword[keyword].daily_stats[date] = {
      clicks: 0,
      impressions: 0,
      weightedPositionSum: 0,
      positionImpressions: 0
    };
  }

  // Aggregate daily stats
  groupedByKeyword[keyword].daily_stats[date].clicks += Number(clicks);
  groupedByKeyword[keyword].daily_stats[date].impressions += Number(impressions);
  groupedByKeyword[keyword].daily_stats[date].weightedPositionSum += Number(position) * Number(impressions);
  groupedByKeyword[keyword].daily_stats[date].positionImpressions += Number(impressions);
}

// Convert to final format with sorted daily arrays
const output = Object.values(groupedByKeyword).map(keywordData => {
  // Convert daily_stats object to sorted array
  const dailyStats = Object.entries(keywordData.daily_stats)
    .map(([date, stats]) => {
      const ctr = stats.impressions > 0 ? (stats.clicks / stats.impressions) * 100 : 0;
      const avgPosition = stats.positionImpressions > 0
        ? stats.weightedPositionSum / stats.positionImpressions
        : 0;

      return {
        date,
        clicks: stats.clicks,
        impressions: stats.impressions,
        ctr: Number(ctr.toFixed(2)),
        position: Number(avgPosition.toFixed(2))
      };
    })
    .sort((a, b) => a.date.localeCompare(b.date)); // Sort by date

  return {
    json: {
      keyword: keywordData.keyword,
      performance: dailyStats
    }
  };
});

return output;

const allQueries = [];

// Loop through the merged mobile and desktop results
for (const item of $input.all()) {
  const relatedSearches = item.json.related_searches || [];

  // Loop through each block in the related_searches array
  for (const searchBlock of relatedSearches) {
    // Case 1: It's a simple text-based search with a 'query' field
    if (searchBlock.query) {
      allQueries.push({ json: { query: searchBlock.query } });
    }
    // Case 2: It's a complex visual block with a nested 'items' array
    else if (searchBlock.items && Array.isArray(searchBlock.items)) {
      for (const subItem of searchBlock.items) {
        if (subItem.name) {
          allQueries.push({ json: { query: subItem.name } });
        }
      }
    }
  }
}

// Return a flat list of items, each with a single 'query' field
return allQueries;

function extractContent(html) {
    // Remove all script and style elements
    html = html.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
               .replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, '')
               .replace(/<dialog\b[^<]*(?:(?!<\/dialog>)<[^<]*)*<\/dialog>/gi, '');

    // Extract only paragraphs and headings with their content
    const contentRegex = /<(p|h[1-6]).*?>(.*?)<\/\1>/gi;
    const matches = [...html.matchAll(contentRegex)];

    // Clean up the extracted content
    return matches.map(match => {
        let content = match[2]
            .replace(/<[^>]+>/g, '')
            .replace(/&nbsp;/g, ' ')
            .replace(/&amp;/g, '&')
            .replace(/&lt;/g, '<')
            .replace(/&gt;/g, '>')
            .replace(/&quot;/g, '"')
            .replace(/\s+/g, ' ')
            .trim();

        return content;
    }).filter(content => content.length > 0).join('\n\n');
}

// Create new array with formatted content
const newItems = $input.all().map(item => {
    return {
        article: {
            url: item.json.article.url,
            title: item.json.article.title,
            content: extractContent(item.json.article.content)
        }
    };
});

return newItems;

